38315ea4-706f-486c-af27-573a3623584e.fn:
async (selectInteraction) => {
        if (selectInteraction.user.id !== interaction.user.id) {
          return selectInteraction.reply({
            content: '❌ Apenas quem usou o comando pode interagir!',
            ephemeral: true
          });
        }

        const escolha = selectInteraction.values[0];
        const embed = {
          gacha: embedGacha,
          informacoes: embedInformacoes,
          aventureiro: embedAventureiro,
          roleplay: embedRoleplay,
          administracao: embedAdministracao
        }[escolha];

        await selectInteraction.update({ embeds: [embed] });
      }

bbf9a998-cb70-419e-81e9-7c0b84f6b1b3.fn:
async (btnInt) => {
        await btnInt.deferUpdate();
        if (btnInt.user.id !== interaction.user.id)
          return btnInt.followUp({ content: "❌ Apenas quem executou o comando pode usar estes botões.", ephemeral: true });
        if (userdb.primogemas < 160)
          return btnInt.followUp({ content: "Uma única invocação custa 160 primogemas.", ephemeral: true });

        const resultado = await Furina.Banner.push(pityData, interaction.user.id, 1, bannerChoice, interaction.guild.id);
        const res = resultado[0];
        const gifUrl = gifs[`1tiro-t${res.raridade}`];

        await btnInt.editReply({ embeds: [new EmbedBuilder().setImage(gifUrl)], components: [], files: [] });

        setTimeout(async () => {
          const embed = new EmbedBuilder()
            .setTitle("**A sorte lança seus dados!**")
            .setDescription(`Você obteve: **${res.nome}** (${res.type}) - ${res.raridade}★`)
            .setColor(res.raridade === 5 ? "#D9B468" : res.raridade === 4 ? "#8A75D1" : "#A0A0A0")
            .setImage(res.raridade < 4 ? null : `attachment://${res.nome}.png`);

          await btnInt.editReply({
            embeds: [embed],
            files: res.raridade < 4 ? [] : [new AttachmentBuilder(`./src/img/banners/${res.type === "Arma" ? "armas" : "personagens"}/${res.nome}.png`)]
          });
        }, 5000);
      }

